mapper.select:
-- 各個任務分數分佈 SELECT subquery.title, subquery.script_id, subquery.task_id, orderlyTotal, relationTotal, CASE WHEN orderlyTotal > 0 AND relationTotal > 0 THEN '結局一' WHEN orderlyTotal > 0 AND relationTotal < 0 THEN '結局二' WHEN orderlyTotal < 0 AND relationTotal > 0 THEN '結局三' WHEN orderlyTotal < 0 AND relationTotal < 0 THEN '結局四' END AS result FROM ( SELECT ws.title, wsq.script_id, wsq.task_id, CASE WHEN SUM(wssc.stu_orderly + wspc.par_orderly) = 0 THEN 1 ELSE SUM(wssc.stu_orderly + wspc.par_orderly) END AS orderlyTotal, CASE WHEN SUM(wssc.stu_relation + wspc.par_relation) = 0 THEN 1 ELSE SUM(wssc.stu_relation + wspc.par_relation) END AS relationTotal FROM wa_script_question wsq JOIN wa_script_parent_config wspc ON wspc.id = wsq.par_ans JOIN wa_script_student_config wssc ON wssc.id = wsq.stu_ans JOIN wa_script ws ON ws.script_id = wsq.script_id GROUP BY wsq.script_id, wsq.task_id, ws.title ) AS subquery WHERE 1=1
AND task_id = #{taskId}
ORDER BY script_id, task_id;
